package koigame.sdk.db;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import koigame.sdk.KMetaData;/** * 数据库操作类 *  *  */public class DBOpenHelper extends SQLiteOpenHelper {	private static String DBNAME = KMetaData.Site + "down.db";	private static final int VERSION = 1;	/**	 * 构造器	 * 	 * @param context	 */	public DBOpenHelper(Context context) {		super(context, DBNAME, null, VERSION);	}	@Override	public void onCreate(SQLiteDatabase db) {		db.execSQL("CREATE TABLE IF NOT EXISTS "				+ KMetaData.Site				+ "_filedownlog (id integer primary key autoincrement, downpath varchar(100), threadid INTEGER, downlength INTEGER)");	}	@Override	public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {		db.execSQL("DROP TABLE IF EXISTS " + KMetaData.Site + "_filedownlog");		onCreate(db);	}}