package koigame.sdk.view;import android.content.Context;import android.util.AttributeSet;import android.view.View;public interface ViewBase<T> {	public void initAttr(Context context, AttributeSet attrs);	public void initLayout();	public void initView();	public void initEvent();	public void initData(T t);	public void setOnViewChangedListener(ViewChangedListener listener);	public void changeView(View view);	public interface ViewChangedListener {		void changed(View view);	}}